import React from 'react';
import axios from 'axios';
import {reduxForm, Field} from 'redux-form';
import TextField from '@material-ui/core/TextField';
import {Box, Grid, makeStyles} from "@material-ui/core";
import Button from '@material-ui/core/Button';

const useStyles = makeStyles((theme) => ({
    form: {
        width: 'auto',
    },
}))

const Contact = (props) => {

    const TextFieldInput = ({input, meta, label}) => {
        // console.log('FIELD COMPONENT PROPS', props);
        return <TextField
            {...input}
            label={label}
        />;
    };
    const LongTextFieldInput = ({input, meta, label}) => {
        // console.log('FIELD COMPONENT PROPS', props);
        return <TextField
            {...input}
            label={label}
            multiline
            rows={4}
        />;
    };

    const {handleSubmit, history} = props;

    // console.log(props);
    const handleContact = async (formValues, dispatch) => {
        console.log(formValues);

        try {
            const res = await axios.post('/api/contact', formValues);
            console.log(res);
            // history.push('/');
        } catch (e) {
            console.log(e);
            throw new Error(e);
        }
    }

    const classes = useStyles();

    return (
        <Box>
            <Grid container>
                <Grid xs={12}>
                    <h1>Contact</h1>
                </Grid>
                <Grid xs={12}>
                    <form noValidate autoComplete="off" className={classes.form}>
                        <div>
                            <Field
                                name='email'
                                label='email'
                                component={TextFieldInput}
                            />
                        </div>
                        <div>
                            <Field
                                name='name'
                                label='name'
                                component={TextFieldInput}
                            />
                        </div>
                        <div>
                            <Field
                                name='message'
                                label='message'
                                component={LongTextFieldInput}
                            />
                        </div>
                        <br/>
                        <div>
                            <Button
                                onClick={handleSubmit(handleContact)}
                                variant="contained"
                                color="primary">
                                Sign in
                            </Button>
                        </div>
                    </form>
                </Grid>

            </Grid>
        </Box>
    )
};


export const WrappedContact = reduxForm({form: 'signInForm'})(Contact);
