import React from 'react';
import CssBaseline from '@material-ui/core/CssBaseline';
import MainAppBar from "./components/utils/AppBar";
import MainDrawer from "./components/utils/Drawer";
import {makeStyles} from "@material-ui/core";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useRouteMatch,
    useParams
} from "react-router-dom";

import { Writing, Coding, Learning } from './components/pages/';

const useStyles = makeStyles(theme => ({
    root: {
        display: 'flex',
    },
    appBar: {
        flexGrow: 1,
        zIndex: theme.zIndex.drawer + 1,
    },
    drawer: {
        flexShrink: 0,
    },
    // content: {
    //     flexGrow: 1,
    //     padding: theme.spacing(3),
    // },
}));


function App() {
    const classes = useStyles();

    return (
        <div className={classes.root}>
            <Router>
                <CssBaseline/>
                <MainAppBar className={classes.appBar}/>
                <MainDrawer className={classes.drawer}/>
                <main className={classes.content}>
                <Switch>
                    <Route path="/coding">
                        <Coding/>
                    </Route>
                    <Route path="/writing">
                        <Writing/>
                    </Route>
                    <Route path="/learning">
                        <Learning/>
                    </Route>
                </Switch>
                </main>

            </Router>
        </div>
        //    AppBar, Drawer, Main Content
    );
}

export default App;
